import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Modal from 'react-modal';

Modal.setAppElement('#root'); // This is for accessibility reasons

const AdminPage = () => {
    const [bookings, setBookings] = useState([]);
    const [confirmedBookings, setConfirmedBookings] = useState([]);
    const [availability, setAvailability] = useState([]);
    const [selectedDate, setSelectedDate] = useState('');
    const [singleRooms, setSingleRooms] = useState(5);
    const [groupRooms, setGroupRooms] = useState(2);

    const [bookingToUpdate, setBookingToUpdate] = useState(null);
    const [updatedBooking, setUpdatedBooking] = useState({
        name: '',
        number: '',
        email: '',
        people: '',
        indate: '',
        outdate: '',
        option: '',
        note: '',
        singleRooms: 1,
    });

    const [isUpdateModalOpen, setIsUpdateModalOpen] = useState(false);
    const [isAvailabilityModalOpen, setIsAvailabilityModalOpen] = useState(false);

    useEffect(() => {
        fetchBookings();
        fetchAvailability();
    }, []);

    const fetchBookings = () => {
        axios.get('http://localhost:3000/api/booking')
            .then((response) => {
                setBookings(response.data.bookings || []);
                setConfirmedBookings(response.data.confirmed || []);
            }).catch(error => {
                console.error("There was an error fetching the bookings!", error);
            });
    };

    const fetchAvailability = () => {
        axios.get('http://localhost:3000/api/availability')
            .then((response) => {
                setAvailability(response.data || []);
            }).catch(error => {
                console.error("There was an error fetching the availability!", error);
            });
    };

    const handleConfirmBooking = (index) => {
        const bookingToConfirm = bookings[index];
        axios.post('http://localhost:3000/api/booking/confirm', bookingToConfirm)
            .then(() => {
                fetchBookings();
                window.location.reload(); // Refresh the page
            })
            .catch((error) => {
                if (error.response && error.response.data) {
                    alert(error.response.data.message);
                } else {
                    alert('Error confirming booking');
                }
            });
    };

    const handleDeclineBooking = (index) => {
        const bookingToDecline = bookings[index];
        axios.post('http://localhost:3000/api/booking/decline', bookingToDecline)
            .then(() => {
                setBookings(bookings.filter((_, i) => i !== index));
            })
            .catch(error => {
                console.error('Error declining booking:', error);
            });
    };

    const handleDeleteRequestBooking = (id) => {
        axios.delete(`http://localhost:3000/api/requestBookings/${id}`)
            .then(() => {
                setBookings(prevBookings => prevBookings.filter(booking => booking._id !== id));
            })
            .catch(error => {
                console.error('Error deleting request booking:', error);
            });
    };

    const handleDeleteConfirmedBooking = (id) => {
        axios.delete(`http://localhost:3000/api/confirmedBookings/${id}`)
            .then(() => {
                setConfirmedBookings(prevConfirmed => prevConfirmed.filter(booking => booking._id !== id));
            })
            .catch(error => {
                console.error('Error deleting confirmed booking:', error);
            });
    };

    const handleDateChange = (e) => {
        const date = e.target.value; // Ensure this is in YYYY-MM-DD format
        setSelectedDate(date);
        axios.get(`http://localhost:3000/api/availability/${date}`)
            .then((response) => {
                if (response.data && response.data.length > 0) {
                    const { singleRooms, groupRooms } = response.data[0];
                    setSingleRooms(singleRooms);
                    setGroupRooms(groupRooms);
                } else {
                    setSingleRooms(5); // Default values when no data found
                    setGroupRooms(2);
                }
            }).catch(error => {
                console.error("Error fetching availability for this date:", error);
                setSingleRooms(5); // Handle error case
                setGroupRooms(2);
            });
    };

    const handleSaveAvailability = () => {
        if (!selectedDate) return;

        axios.put(`http://localhost:3000/api/availability/${selectedDate}`, { singleRooms, groupRooms })
            .then(() => {
                fetchAvailability();
                alert('Availability updated successfully');
                setIsAvailabilityModalOpen(false); // Close the modal
            })
            .catch((error) => {
                console.error('Error updating availability:', error);
                alert('Failed to update availability');
            });
    };

    const renderDateOptions = () => {
        const today = new Date();
        const options = [];
        for (let i = 0; i < 30; i++) {
            const date = new Date(today);
            date.setDate(today.getDate() + i);
            options.push(date.toISOString().split('T')[0]);
        }
        return options.map(date => <option key={date} value={date}>{date}</option>);
    };

    const handleUpdateBooking = (booking) => {
        setBookingToUpdate(booking);
        setUpdatedBooking({
            name: booking.name,
            number: booking.number,
            email: booking.email,
            people: booking.people,
            indate: booking.indate,
            outdate: booking.outdate,
            option: booking.option,
            note: booking.note,
            singleRooms: booking.singleRooms || 1,
        });
        setIsUpdateModalOpen(true);
    };

    const handleUpdateClose = () => {
        setBookingToUpdate(null);
        setIsUpdateModalOpen(false);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUpdatedBooking({ ...updatedBooking, [name]: value });
    };

    const handleUpdateSubmit = (e) => {
        e.preventDefault();
        if (bookingToUpdate) {
            let endpoint;
            if (bookingToUpdate.confirmed) {
                endpoint = `http://localhost:3000/api/confirmedBookings/${bookingToUpdate._id}`;
                axios.put(endpoint, updatedBooking)
                    .then(response => {
                        setConfirmedBookings(prevConfirmed => prevConfirmed.map(booking => {
                            if (booking._id === bookingToUpdate._id) {
                                return response.data;
                            }
                            return booking;
                        }));
                        setBookingToUpdate(null); // Close the form/modal
                        setIsUpdateModalOpen(false); // Close the modal
                    })
                    .catch(error => {
                        console.error('Error updating confirmed booking:', error);
                        alert('Error updating confirmed booking');
                    });
            } else {
                endpoint = `http://localhost:3000/api/requestBookings/${bookingToUpdate._id}`;
                axios.put(endpoint, updatedBooking)
                    .then(response => {
                        setBookings(prevBookings => prevBookings.map(booking => {
                            if (booking._id === bookingToUpdate._id) {
                                return response.data;
                            }
                            return booking;
                        }));
                        setBookingToUpdate(null); // Close the form/modal
                        setIsUpdateModalOpen(false); // Close the modal
                    })
                    .catch(error => {
                        console.error('Error updating request booking:', error);
                        alert('Error updating request booking');
                    });
            }
        }
    };

    return (
        <div className="container">
            <h1>Admin Page</h1>

            {/* Requests Table */}
            <h2>Requests</h2>
            <table className="table">
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Check In</th>
                        <th>Check Out</th>
                        <th>People</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {bookings.map((booking, index) => (
                        <tr key={index}>
                            <td>{booking.name}</td>
                            <td>{booking.indate}</td>
                            <td>{booking.outdate}</td>
                            <td>{booking.people}</td>
                            <td>
                                <button className="btn btn-success" onClick={() => handleConfirmBooking(index)}>Confirm</button>
                                <button className="btn btn-danger" onClick={() => handleDeleteRequestBooking(booking._id)}>Delete</button>
                                <button className="btn btn-warning" onClick={() => handleUpdateBooking(booking)}>Update</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Confirmed Bookings Table */}
            <h2>Confirmed Bookings</h2>

            <table className="table">
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Check In</th>
                        <th>Check Out</th>
                        <th>People</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {confirmedBookings.map((booking, index) => (
                        <tr key={index}>
                            <td>{booking.name}</td>
                            <td>{booking.indate}</td>
                            <td>{booking.outdate}</td>
                            <td>{booking.people}</td>
                            <td>
                                <button className="btn btn-warning" onClick={() => handleUpdateBooking(booking)}>Update</button>
                                <button className="btn btn-danger" onClick={() => handleDeleteConfirmedBooking(booking._id)}>Delete</button>
                                <button className="btn btn-warning" onClick={() => handleUpdateBooking(booking)}>Update</button>
                                <button className="btn btn-danger" onClick={() => handleDeleteConfirmedBooking(booking._id)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Update Booking Modal */}
            <Modal
                isOpen={isUpdateModalOpen}
                onRequestClose={handleUpdateClose}
                contentLabel="Update Booking"
            >
                <h2>Update Booking</h2>
                <form onSubmit={handleUpdateSubmit}>
                    <div className="mb-3">
                        <label className="form-label">Name:</label>
                        <input
                            type="text"
                            className="form-control"
                            name="name"
                            value={updatedBooking.name}
                            onChange={handleInputChange}
                        />
                    </div>
                    <div className="mb-3">
                        <label className="form-label">Number:</label>
                        <input
                            type="text"
                            className="form-control"
                            name="number"
                            value={updatedBooking.number}
                            onChange={handleInputChange}
                        />
                    </div>
                    <div className="mb-3">
                        <label className="form-label">Email:</label>
                        <input
                            type="email"
                            className="form-control"
                            name="email"
                            value={updatedBooking.email}
                            onChange={handleInputChange}
                        />
                    </div>
                    <div className="mb-3">
                        <label className="form-label">People:</label>
                        <input
                            type="number"
                            className="form-control"
                            name="people"
                            value={updatedBooking.people}
                            onChange={handleInputChange}
                        />
                    </div>
                    <div className="mb-3">
                        <label className="form-label">Check-in Date:</label>
                        <input
                            type="date"
                            className="form-control"
                            name="indate"
                            value={updatedBooking.indate}
                            onChange={handleInputChange}
                        />
                    </div>
                    <div className="mb-3">
                        <label className="form-label">Check-out Date:</label>
                        <input
                            type="date"
                            className="form-control"
                            name="outdate"
                            value={updatedBooking.outdate}
                            onChange={handleInputChange}
                        />
                    </div>
                    <div className="mb-3">
                        <label className="form-label">Option:</label>
                        <select
                            className="form-control"
                            name="option"
                            value={updatedBooking.option}
                            onChange={handleInputChange}
                        >
                            <option value="Option 1">Option 1</option>
                            <option value="Option 2">Option 2</option>
                        </select>
                    </div>
                    <div className="mb-3">
                        <label className="form-label">Note:</label>
                        <textarea
                            className="form-control"
                            name="note"
                            value={updatedBooking.note}
                            onChange={handleInputChange}
                        />
                    </div>
                    <div className="mb-3">
                        <label className="form-label">Single Rooms:</label>
                        <input
                            type="number"
                            className="form-control"
                            name="singleRooms"
                            value={updatedBooking.singleRooms}
                            onChange={handleInputChange}
                        />
                    </div>
                    <button type="submit" className="btn btn-primary">Update Booking</button>
                </form>
            </Modal>

            {/* Availability Modal */}
            <Modal
                isOpen={isAvailabilityModalOpen}
                onRequestClose={() => setIsAvailabilityModalOpen(false)}
                contentLabel="Edit Availability"
            >
                <h2>Edit Availability</h2>
                <form>
                    <div className="mb-3">
                        <label className="form-label">Date:</label>
                        <select
                            className="form-control"
                            value={selectedDate}
                            onChange={handleDateChange}
                        >
                            {renderDateOptions()}
                        </select>
                    </div>
                    <div className="mb-3">
                        <label className="form-label">Single Rooms Available:</label>
                        <input
                            type="number"
                            className="form-control"
                            value={singleRooms}
                            onChange={(e) => setSingleRooms(e.target.value)}
                        />
                    </div>
                    <div className="mb-3">
                        <label className="form-label">Group Rooms Available:</label>
                        <input
                            type="number"
                            className="form-control"
                            value={groupRooms}
                            onChange={(e) => setGroupRooms(e.target.value)}
                        />
                    </div>
                    <button type="button" className="btn btn-primary" onClick={handleSaveAvailability}>Save Availability</button>
                </form>
            </Modal>

            {/* Buttons */}
            <div className="mb-3">
                <button className="btn btn-primary me-3" onClick={() => setIsAvailabilityModalOpen(true)}>Edit Availability</button>
                <button className="btn btn-danger">Logout</button>
            </div>
        </div>
    );
};

export default AdminPage;
