import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminPage = () => {
    const [bookings, setBookings] = useState([]);
    const [confirmedBookings, setConfirmedBookings] = useState([]);
    const [availability, setAvailability] = useState([]);
    const [selectedDate, setSelectedDate] = useState(null);
    const [singleRooms, setSingleRooms] = useState(5);
    const [groupRooms, setGroupRooms] = useState(2);

  useEffect(() => {
    axios.get('http://localhost:3000/api/booking').then((response) => {
      setBookings(response.data.bookings || []);
      setConfirmedBookings(response.data.confirmed || []);
    }).catch(error => {
      console.error("There was an error fetching the bookings!", error);
    });

    axios.get('http://localhost:3000/api/availability').then((response) => {
      setAvailability(response.data || []);
    }).catch(error => {
      console.error("There was an error fetching the availability!", error);
    });
  }, []);

  const handleConfirm = (index) => {
    const bookingToConfirm = bookings[index];
    axios.post('http://localhost:3000/api/booking/confirm', bookingToConfirm).then(() => {
      setConfirmedBookings([...confirmedBookings, bookingToConfirm]);
      setBookings(bookings.filter((_, i) => i !== index));
    });
  };

  const handleDecline = (index) => {
    const bookingToDecline = bookings[index];
    axios.post('http://localhost:3000/api/booking/decline', bookingToDecline).then(() => {
      setBookings(bookings.filter((_, i) => i !== index));
    });
  };

  const handleDateChange = (e) => {
    const date = e.target.value;
    setSelectedDate(date);
    axios.get(`http://localhost:3000/api/availability/${date}`).then((response) => {
      setSingleRooms(response.data.singleRooms);
      setGroupRooms(response.data.groupRooms);
    }).catch(error => {
      console.error("There was an error fetching the availability for this date!", error);
      setSingleRooms(5);
      setGroupRooms(2);
    });
  };
  const handleSave = () => {
    if (!selectedDate) return;

    axios.post(`http://localhost:3000/api/availability/${selectedDate}`, { singleRooms, groupRooms })
      .then((response) => {
        console.log('Availability updated successfully:', response.data);
        axios.get('http://localhost:3000/api/availability')
          .then((response) => {
            setAvailability(response.data || []);
          })
          .catch(error => {
            console.error("There was an error fetching the availability!", error);
          });
        alert('Availability updated successfully');
      })
      .catch((error) => {
        console.error('Error updating availability:', error);
        alert('Failed to update availability');
      });
  };


  const renderDateOptions = () => {
    const today = new Date();
    const options = [];
    for (let i = 0; i < 30; i++) {
      const date = new Date(today);
      date.setDate(today.getDate() + i);
      options.push(date.toISOString().split('T')[0]);
    }
    return options.map(date => <option key={date} value={date}>{date}</option>);
  };

  return (
    <div className="container">
      <h1>Admin Page</h1>

      <h2>Requests</h2>
      <table className="table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Check In</th>
            <th>Check Out</th>
            <th>People</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {bookings.map((booking, index) => (
            <tr key={index}>
              <td>{booking.name}</td>
              <td>{booking.indate}</td>
              <td>{booking.outdate}</td>
              <td>{booking.people}</td>
              <td>
                <button className="btn btn-success" onClick={() => handleConfirm(index)}>Confirm</button>
                <button className="btn btn-danger" onClick={() => handleDecline(index)}>Decline</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>Confirmed Bookings</h2>
      <table className="table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Check In</th>
            <th>Check Out</th>
            <th>People</th>
          </tr>
        </thead>
        <tbody>
          {confirmedBookings.map((booking, index) => (
            <tr key={index}>
              <td>{booking.name}</td>
              <td>{booking.indate}</td>
              <td>{booking.outdate}</td>
              <td>{booking.people}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>Manage Room Availability</h2>
      <div className="mb-3">
        <label className="form-label">Select Date</label>
        <select className="form-control" onChange={handleDateChange}>
          <option value="">Select a date</option>
          {renderDateOptions()}
        </select>
      </div>

      {selectedDate && (
        <div>
          <div className="mb-3">
            <label className="form-label">Single Rooms Available</label>
            <input
              type="number"
              className="form-control"
              value={singleRooms}
              onChange={(e) => setSingleRooms(Number(e.target.value))}
              min="0"
              max="5"
            />
          </div>

          <div className="mb-3">
            <label className="form-label">Group Rooms Available</label>
            <input
              type="number"
              className="form-control"
              value={groupRooms}
              onChange={(e) => setGroupRooms(Number(e.target.value))}
              min="0"
              max="2"
            />
          </div>

          <button className="btn btn-primary" onClick={handleSave}>Save</button>
        </div>
      )}

      <h2>Room Availability</h2>
      <table className="table">
        <thead>
          <tr>
            <th>Date</th>
            <th>Single Rooms Available</th>
            <th>Group Rooms Available</th>
          </tr>
        </thead>
        <tbody>
          {availability.map((day, index) => (
            <tr key={index}>
              <td>{new Date(day.date).toLocaleDateString()}</td>
              <td>{day.singleRooms}</td>
              <td>{day.groupRooms}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminPage;
