import React, { useState, useEffect } from 'react';
import axios from 'axios';


const BookingSection = () => {

    const [formData, setFormData] = useState({
        name: '',
        number: '',
        email: '',
        people: '',
        indate: '',
        outdate: '',
        option: '',
        note: ''
    });

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
          await axios.post('http://localhost:3000/api/book', formData);
          alert('Booking request submitted!');
        } catch (error) {
          console.error('There was an error!', error);
        }
    }




    const [selectedDate, setSelectedDate] = useState(null);
    const [singleRooms, setSingleRooms] = useState(null);
    const [groupRooms, setGroupRooms] = useState(null);
    const [availability, setAvailability] = useState([]);

    useEffect(() => {
      axios.get('http://localhost:3000/api/availability').then((response) => {
        setAvailability(response.data || []);
      }).catch(error => {
        console.error("There was an error fetching the availability!", error);
      });
    }, []);


    const handleDateChange = (e) => {
        const date = e.target.value;
        setSelectedDate(date);
        axios.get(`http://localhost:3000/api/availability/${date}`).then((response) => {
          setSingleRooms(response.data.singleRooms);
          setGroupRooms(response.data.groupRooms);
        }).catch(error => {
          console.error("There was an error fetching the availability for this date!", error);
          setSingleRooms(null);
          setGroupRooms(null);
        });
      };

      const renderDateOptions = () => {
        const today = new Date();
        const options = [];
        for (let i = 0; i < 30; i++) {
          const date = new Date(today);
          date.setDate(today.getDate() + i);
          options.push(date.toISOString().split('T')[0]);
        }
        return options.map(date => <option key={date} value={date}>{date}</option>);
      };





  return (
    <div>


        <div className="overlay">
            <img className="room-image" src={`${process.env.PUBLIC_URL}/vivian-source/book.jpg`} alt="Room" />
            <div className="text-overlay">Đặt Phòng</div>   
        </div>



        <div className="container">
      <h1>Check Room Availability</h1>

      <div className="mb-3">
        <label className="form-label">Select Date</label>
        <select className="form-control" onChange={handleDateChange}>
          <option value="">Select a date</option>
          {renderDateOptions()}
        </select>
      </div>

      {selectedDate && (
        <div>
          <h2>Availability for {new Date(selectedDate).toLocaleDateString()}</h2>
          {singleRooms !== null && groupRooms !== null ? (
            <div>
              <p>Single Rooms Available: {singleRooms}</p>
              <p>Group Rooms Available: {groupRooms}</p>
            </div>
          ) : (
            <p>Loading availability...</p>
          )}
        </div>
      )}
    </div>




    <div className="container d-flex justify-content-center align-items-center vh-100">
      <div className="card" style={{ width: '30rem' }}>
        <div className="card-body">
          <h5 className="card-title">Gửi Yêu Cầu Đặt Phòng</h5>



          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label className="form-label">Họ Và Tên</label>
              <input type="text" name="name" className="form-control" onChange={handleChange} required />
            </div>
            <div className="mb-3">
              <label className="form-label">Số Điện Thoại</label>
              <input type="tel" name="number" className="form-control" onChange={handleChange} required />
            </div>
            <div className="mb-3">
              <label className="form-label">Email</label>
              <input type="email" name="email" className="form-control" onChange={handleChange} required />
            </div>
            <div className="mb-3">
              <label className="form-label">Số Lượng Người</label>
              <input type="number" name="people" className="form-control" onChange={handleChange} required />
            </div>
            <div className="mb-3">
              <label className="form-label">Ngày Đặt Mong Muốn Check In</label>
              <input type="date" name="indate" className="form-control" onChange={handleChange} required />
            </div>
            <div className="mb-3">
              <label className="form-label">Ngày Đặt Mong Muốn Check Out</label>
              <input type="date" name="outdate" className="form-control" onChange={handleChange} required />
            </div>

            <div className="mb-3 form-check">
                <label className="form-label">Lựa Chọn Booking</label>
                <div style={{ display: 'flex', justifyContent: 'space-between', width: '300px' }}>
                <div>
                    <input type="radio" id="option1" name="option" value="Nguyen Căn" onChange={handleChange} required />
                    <label htmlFor="option1">Nguyên Căn</label>
                </div>
                <div>
                    <input type="radio" id="option2" name="option" value="6 Phòng" onChange={handleChange} required />
                    <label htmlFor="option2">6 Phòng</label>
                </div>
                <div>
                    <input type="radio" id="option3" name="option" value="Phòng Lẻ" onChange={handleChange} required />
                    <label htmlFor="option3">Phòng Lẻ</label>
                </div>
            </div>

            <div className="mb-3">
                <label className="form-label">Ghi Chú</label>
                <input type="text" name="note" className="form-control" onChange={handleChange}/>
            </div>

            </div>

            <button type="submit" className="btn btn-primary">Submit</button>
          </form>



        </div>
      </div>
    </div>




    </div>
  );
};

export default BookingSection;
