import React, { useState } from 'react';
// import './EntertainmentSection.css';

const images1 = [
  `${process.env.PUBLIC_URL}/vivian-source/billy2.jpg`,
  `${process.env.PUBLIC_URL}/vivian-source/billy1.jpg`,
  `${process.env.PUBLIC_URL}/vivian-source/billy3.jpg`
];

const images2 = [
  `${process.env.PUBLIC_URL}/vivian-source/pool2.jpg`,
  `${process.env.PUBLIC_URL}/vivian-source/pool1.jpg`,
  `${process.env.PUBLIC_URL}/vivian-source/pool3.jpg`,
  `${process.env.PUBLIC_URL}/vivian-source/pool4.jpg`
];

const images3 = [
  `${process.env.PUBLIC_URL}/vivian-source/sauna1.jpeg`,
  `${process.env.PUBLIC_URL}/vivian-source/sauna2.jpeg`,
  `${process.env.PUBLIC_URL}/vivian-source/sauna3.jpg`,
  `${process.env.PUBLIC_URL}/vivian-source/sauna4.jpg`
];

const ImageSlider = ({ images }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const handlePrevious = () => {
    setCurrentIndex((prevIndex) => (prevIndex === 0 ? images.length - 1 : prevIndex - 1));
  };

  const handleNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex === images.length - 1 ? 0 : prevIndex + 1));
  };

  return (
    <div className="slideshow-container">
      <button className='prev' onClick={handlePrevious}>&#10094;</button>
      <div className="slideshow-images">
        <img className='demo-img' src={images[currentIndex]} alt={`Slide ${currentIndex + 1}`} />
      </div>
      <button className="next" onClick={handleNext}>&#10095;</button>
    </div>
  );
};

const EntertainmentSection = () => {
  return (
    <section className="entertainment">
      <div className='entertainment-content'>
      <h2>Giải Trí</h2>
      <div className="content-row">
        <ImageSlider images={images1} className="img" />
        <div className="text-section">
          <h3>Phòng giải trí</h3>
          <p>Bao gồm Bi-a, Bi lắc, Karaoke</p>
        </div>
      </div>
      <div className="content-row section2">
        <ImageSlider images={images2} className="img2"/>
        <div className="text-section2">
          <h3>Bể bơi vô cực</h3>
          <p>Bể bơi với chiều dài 30m, view vườn hoa rực rỡ</p>
        </div>
      </div>
      <div className="content-row">
      <ImageSlider images={images3} className="img" />
        <div className="text-section">
          <h3>Sông Hơi</h3>
          <p>Chạy 24/7 với 2 loại sông khác nhau</p>
        </div>
      </div>
      </div>
    </section>
  );
};

export default EntertainmentSection;
