import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Modal from 'react-modal';

Modal.setAppElement('#root'); // This is for accessibility reasons

const AdminPage = () => {
    const [bookings, setBookings] = useState([]);
    const [confirmedBookings, setConfirmedBookings] = useState([]);
    const [availability, setAvailability] = useState([]);
    const [selectedDate, setSelectedDate] = useState('');
    const [singleRooms, setSingleRooms] = useState(5);
    const [groupRooms, setGroupRooms] = useState(2);

    const [bookingToUpdate, setBookingToUpdate] = useState(null);
    const [updatedBooking, setUpdatedBooking] = useState({
        name: '',
        number: '',
        email: '',
        people: '',
        indate: '',
        outdate: '',
        option: '',
        note: '',
        singleRooms: 1,
    });

    const [isUpdateModalOpen, setIsUpdateModalOpen] = useState(false);
    const [isAvailabilityModalOpen, setIsAvailabilityModalOpen] = useState(false);

    useEffect(() => {
        axios.get('https://vivian-garden-back.vercel.app/api/booking')
            .then((response) => {
                setBookings(response.data.bookings || []);
                setConfirmedBookings(response.data.confirmed || []);
            }).catch(error => {
                console.error("There was an error fetching the bookings!", error);
            });

        axios.get('https://vivian-garden-back.vercel.app/api/availability')
            .then((response) => {
                setAvailability(response.data || []);
            }).catch(error => {
                console.error("There was an error fetching the availability!", error);
            });
    }, []);

    const handleConfirm = (index) => {
        const bookingToConfirm = bookings[index];
        axios.post('https://vivian-garden-back.vercel.app/api/booking/confirm', bookingToConfirm)
            .then(() => {
                axios.get('https://vivian-garden-back.vercel.app/api/booking')
                    .then((response) => {
                        setBookings(response.data.bookings || []);
                        setConfirmedBookings(response.data.confirmed || []);
                        setAvailability(response.data.availability || []); // Update availability state if needed
                    }).catch(error => {
                        console.error("There was an error fetching the bookings!", error);
                    });
            })
            .catch((error) => {
                if (error.response && error.response.data) {
                    alert(error.response.data.message);
                } else {
                    alert('Error confirming booking');
                }
            });
    };

    const handleDecline = (index) => {
        const bookingToDecline = bookings[index];
        axios.post('https://vivian-garden-back.vercel.app/api/booking/decline', bookingToDecline)
            .then(() => {
                setBookings(bookings.filter((_, i) => i !== index));
            });
    };

    const handleDateChange = (e) => {
        const date = e.target.value; // Ensure this is in YYYY-MM-DD format
        setSelectedDate(date);
        axios.get(`https://vivian-garden-back.vercel.app/api/availability/${date}`)
            .then((response) => {
                if (response.data && response.data.length > 0) {
                    const { singleRooms, groupRooms } = response.data[0];
                    setSingleRooms(singleRooms);
                    setGroupRooms(groupRooms);
                } else {
                    setSingleRooms(5); // Default values when no data found
                    setGroupRooms(2);
                }
            }).catch(error => {
                console.error("Error fetching availability for this date:", error);
                setSingleRooms(5); // Handle error case
                setGroupRooms(2);
            });
    };

    const handleSave = () => {
        if (!selectedDate) return;

        axios.put(`https://vivian-garden-back.vercel.app/api/availability/${selectedDate}`, { singleRooms, groupRooms })
            .then((response) => {
                console.log('Availability updated successfully:', response.data);
                axios.get('https://vivian-garden-back.vercel.app/api/availability')
                    .then((response) => {
                        setAvailability(response.data || []);
                    })
                    .catch(error => {
                        console.error("There was an error fetching the availability!", error);
                    });
                alert('Availability updated successfully');
                setIsAvailabilityModalOpen(false); // Close the modal
            })
            .catch((error) => {
                console.error('Error updating availability:', error);
                alert('Failed to update availability');
            });
    };

    const renderDateOptions = () => {
        const today = new Date();
        const options = [];
        for (let i = 0; i < 30; i++) {
            const date = new Date(today);
            date.setDate(today.getDate() + i);
            options.push(date.toISOString().split('T')[0]);
        }
        return options.map(date => <option key={date} value={date}>{date}</option>);
    };

    const handleUpdateBooking = (bookingId, updatedData) => {
        axios.put(`https://vivian-garden-back.vercel.app/api/confirmedBookings/${bookingId}`, updatedData)
            .then((response) => {
                setConfirmedBookings(confirmedBookings.map(booking => booking._id === bookingId ? response.data : booking));
                setBookingToUpdate(null); // Close the form/modal
                setIsUpdateModalOpen(false); // Close the modal
            })
            .catch((error) => {
                console.error(error, 'Error updating booking');
            });
    };

    const handleUpdateSubmit = (e) => {
        e.preventDefault();
        if (bookingToUpdate) {
            handleUpdateBooking(bookingToUpdate._id, updatedBooking);
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUpdatedBooking({ ...updatedBooking, [name]: value });
    };

    const handleUpdateClick = (booking) => {
        setBookingToUpdate(booking);
        setUpdatedBooking({
            name: booking.name,
            number: booking.number,
            email: booking.email,
            people: booking.people,
            indate: booking.indate,
            outdate: booking.outdate,
            option: booking.option,
            note: booking.note,
            singleRooms: booking.singleRooms || 1,
        });
        setIsUpdateModalOpen(true);
    };

    const handleUpdateClose = () => {
        setBookingToUpdate(null);
        setIsUpdateModalOpen(false);
    };

    const handleDeleteConfirmedBooking = (id) => {
        axios.delete(`https://vivian-garden-back.vercel.app/api/confirmedBookings/${id}`)
            .then(() => {
                setConfirmedBookings(confirmedBookings.filter(booking => booking._id !== id));
            })
            .catch(error => {
                console.error('Error deleting confirmed booking:', error);
            });
    };

    return (
        <div className="container">
            <h1>Admin Page</h1>

            {/* Requests Table */}
            <h2>Requests</h2>
            <table className="table">
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Check In</th>
                        <th>Check Out</th>
                        <th>People</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {bookings.map((booking, index) => (
                        <tr key={index}>
                            <td>{booking.name}</td>
                            <td>{booking.indate}</td>
                            <td>{booking.outdate}</td>
                            <td>{booking.people}</td>
                            <td>
                                <button className="btn btn-success" onClick={() => handleConfirm(index)}>Confirm</button>
                                <button className="btn btn-danger" onClick={() => handleDecline(index)}>Decline</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Confirmed Bookings Table */}
            <h2>Confirmed Bookings</h2>
            <table className="table">
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Check In</th>
                        <th>Check Out</th>
                        <th>People</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {confirmedBookings.map((booking, index) => (
                        <tr key={index}>
                            <td>{booking.name}</td>
                            <td>{booking.indate}</td>
                            <td>{booking.outdate}</td>
                            <td>{booking.people}</td>
                            <td>
                                <button className="btn btn-warning" onClick={() => handleUpdateClick(booking)}>Update</button>
                                <button className="btn btn-danger" onClick={() => handleDeleteConfirmedBooking(booking._id)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Update Booking Modal */}
            <Modal
                isOpen={isUpdateModalOpen}
                onRequestClose={handleUpdateClose}
                contentLabel="Update Booking Modal"
                className="modal-dialog"
                overlayClassName="modal-backdrop"
            >
                <div className="modal-content">
                    <div className="modal-header">
                        <h5 className="modal-title">Update Booking</h5>
                        <button type="button" className="close" onClick={handleUpdateClose}>
                            <span>&times;</span>
                        </button>
                    </div>
                    <div className="modal-body">
                        {bookingToUpdate && (
                            <form onSubmit={handleUpdateSubmit}>
                                <div className="form-group">
                                    <label>Name:</label>
                                    <input type="text" name="name" value={updatedBooking.name} onChange={handleInputChange} className="form-control" required />
                                </div>
                                <div className="form-group">
                                    <label>Number:</label>
                                    <input type="text" name="number" value={updatedBooking.number} onChange={handleInputChange} className="form-control" required />
                                </div>
                                <div className="form-group">
                                    <label>Email:</label>
                                    <input type="email" name="email" value={updatedBooking.email} onChange={handleInputChange} className="form-control" required />
                                </div>
                                <div className="form-group">
                                    <label>People:</label>
                                    <input type="number" name="people" value={updatedBooking.people} onChange={handleInputChange} className="form-control" required />
                                </div>
                                <div className="form-group">
                                    <label>Check In:</label>
                                    <input type="date" name="indate" value={updatedBooking.indate} onChange={handleInputChange} className="form-control" required />
                                </div>
                                <div className="form-group">
                                    <label>Check Out:</label>
                                    <input type="date" name="outdate" value={updatedBooking.outdate} onChange={handleInputChange} className="form-control" required />
                                </div>
                                <div className="form-group">
                                    <label>Option:</label>
                                    <input type="text" name="option" value={updatedBooking.option} onChange={handleInputChange} className="form-control" />
                                </div>
                                <div className="form-group">
                                    <label>Note:</label>
                                    <input type="text" name="note" value={updatedBooking.note} onChange={handleInputChange} className="form-control" />
                                </div>
                                <div className="form-group">
                                    <label>Single Rooms:</label>
                                    <input type="number" name="singleRooms" value={updatedBooking.singleRooms} onChange={handleInputChange} className="form-control" />
                                </div>
                                <div className="modal-footer">
                                    <button type="submit" className="btn btn-primary">Save</button>
                                    <button type="button" className="btn btn-secondary" onClick={handleUpdateClose}>Cancel</button>
                                </div>
                            </form>
                        )}
                    </div>
                </div>
            </Modal>

            {/* Availability Management */}
            <h2>Manage Availability</h2>
            <button className="btn btn-primary" onClick={() => setIsAvailabilityModalOpen(true)}>Set Availability</button>

            {/* Availability Modal */}
                            <Modal
                    isOpen={isAvailabilityModalOpen}
                    onRequestClose={() => setIsAvailabilityModalOpen(false)}
                    contentLabel="Set Availability Modal"
                    className="modal-dialog"
                    overlayClassName="modal-backdrop"
                >
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title">Set Availability</h5>
                            <button type="button" className="close" onClick={() => setIsAvailabilityModalOpen(false)}>
                                <span>&times;</span>
                            </button>
                        </div>
                        <div className="modal-body">
                            <form>
                                <div className="form-group">
                                    <label>Date:</label>
                                    <select value={selectedDate} onChange={handleDateChange} className="form-control">
                                        <option value="">Select a date</option>
                                        {renderDateOptions()}
                                    </select>
                                </div>
                                <div className="form-group">
                                    <label>Single Rooms:</label>
                                    <input type="number" value={singleRooms} onChange={(e) => setSingleRooms(e.target.value)} className="form-control" />
                                </div>
                                <div className="form-group">
                                    <label>Group Rooms:</label>
                                    <input type="number" value={groupRooms} onChange={(e) => setGroupRooms(e.target.value)} className="form-control" />
                                </div>
                                <div className="modal-footer">
                                    <button type="button" className="btn btn-primary" onClick={handleSave}>Save</button>
                                    <button type="button" className="btn btn-secondary" onClick={() => setIsAvailabilityModalOpen(false)}>Cancel</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </Modal>
        </div>
    );
};

export default AdminPage;
