import React, {useEffect} from "react";
import { Link } from 'react-router-dom';


const RoomTop = () => {

  useEffect(() => {
    const hasReloaded = localStorage.getItem('hasReloaded');

    if (!hasReloaded) {
      localStorage.setItem('hasReloaded', 'true');
      window.scrollTo(0, 0);
      window.location.reload();
    }

    // Clean up the local storage when the component unmounts
    return () => {
      localStorage.removeItem('hasReloaded');
    };
  }, []);

    return (
<section>
  <div className="overlay">
    <img className="room-image" src={`${process.env.PUBLIC_URL}/vivian-source/room2.jpeg`} alt="Room" loading />
    <div className="text-overlay">Phòng</div>
  </div>

  <div className="card-grid">
    <div className="card">


      {/* <a href="/group-room-view">
