import React, { useState } from 'react';
import axios from 'axios';


const BookingSection = () => {

    const [formData, setFormData] = useState({
        name: '',
        number: '',
        email: '',
        people: '',
        date: ''
    });

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
          await axios.post('http://localhost:5000/api/book', formData);
          alert('Booking request submitted!');
        } catch (error) {
          console.error('There was an error!', error);
        }
    }





  return (
    <div>
        <div className="overlay">
            <img className="room-image" src={`${process.env.PUBLIC_URL}/vivian-source/book.jpg`} alt="Room" />
            <div className="text-overlay">Đặt Phòng</div>   
        </div>



        <form onSubmit={handleSubmit}>
            <div class="mb-3">
                <label for="exampleInputPassword1" class="form-label">Họ Và Tên</label>
                <input type="password" class="form-control" id="exampleInputPassword1"/>
            </div>
            <div class="mb-3">
                <label for="exampleInputPassword1" class="form-label">Số Điện Thoại</label>
                <input type="password" class="form-control" id="exampleInputPassword1"/>
            </div>
            <div class="mb-3">
                <label for="exampleInputEmail1" class="form-label">Email</label>
                <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp"/>
                <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
            </div>
            <div class="mb-3">
                <label for="exampleInputPassword1" class="form-label">Số Lượng Người</label>
                <input type="password" class="form-control" id="exampleInputPassword1"/>
            </div>
            <div class="mb-3">
                <label for="exampleInputPassword1" class="form-label">Password</label>
                <input type="password" class="form-control" id="exampleInputPassword1"/>
            </div>

            {/* <div class="mb-3 form-check">
                <input type="checkbox" class="form-check-input" id="exampleCheck1"/>
                <label class="form-check-label" for="exampleCheck1">Check me out</label>
            </div> */}
            <button type="submit" class="btn btn-primary">Submit</button>
        </form>




    </div>
  );
};

export default BookingSection;
