// src/components/AdminPage.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminPage = () => {
  const [bookings, setBookings] = useState([]);
  const [confirmedBookings, setConfirmedBookings] = useState([]);


  useEffect(() => {
    // Fetch booking requests from the backend (add this endpoint in the backend)
    axios.get('http://localhost:3000/api/booking').then((response) => {
      setBookings(response.data);
      setConfirmedBookings(response.data.confirmed);

    });
  }, []);

  const handleConfirm = (index) => {
    const bookingToConfirm = bookings[index];
    axios.post('http://localhost:5000/api/booking/confirm', bookingToConfirm).then(() => {
      // Remove the booking from requests and add it to confirmed bookings
      setConfirmedBookings([...confirmedBookings, bookingToConfirm]);
      setBookings(bookings.filter((_, i) => i !== index));
    });
  };

  return (
    <div>
      <h1>Admin Page</h1>

      <h2>Request</h2>
      <ul>
        {bookings.map((booking, index) => (
          <li key={index}>
            {booking.name} - {booking.indate} - {booking.outdate} - {booking.people} people
          </li>
        ))}
      </ul>

      <h2>Confirmed Booking</h2>

    </div>
  );
};

export default AdminPage;
