import React, { useState } from 'react';
import axios from 'axios';

const BookingSection = () => {
    const [formData, setFormData] = useState({
        name: '',
        number: '',
        email: '',
        people: '',
        indate: '',
        outdate: '',
        option: '',
        note: '',
        singleRooms: 1
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://localhost:3000/api/book', formData);
            alert('Booking request submitted!');
            window.location.reload(); // Refresh the page
        } catch (error) {
            console.error('There was an error!', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            {/* Your form fields here */}
            <div className="mb-3">
                <label className="form-label">Name</label>
                <input type="text" name="name" className="form-control" value={formData.name} onChange={handleChange} />
            </div>
            {/* Other form fields */}
            {formData.option === 'Phòng Lẻ' && (
                <div className="mb-3">
                    <label className="form-label">Number of Single Rooms</label>
                    <input type="number" name="singleRooms" className="form-control" value={formData.singleRooms} onChange={handleChange} />
                </div>
            )}
            <button type="submit" className="btn btn-primary">Submit</button>
        </form>
    );
};

export default BookingSection;
